    this.appState$ = this.serverService.servers$.pipe(map(response => {
      this.notifier.onDefault(response.message);
      this.dataSubject.next(response);
      return {
        dataState: DataState.LOADED_STATE,
        appData: Object.assign(Object.assign({}, response), {
          data: {
            servers: response.data.servers.reverse()
          }
        })
      };
    }), startWith({
      dataState: DataState.LOADING_STATE
    }), catchError(error => {
      this.notifier.onError(error);
      return of({
        dataState: DataState.ERROR_STATE,
        error
      });
    }));
  }

  pingServer(ipAddress) {
    this.filterSubject.next(ipAddress);
    this.appState$ = this.serverService.ping$(ipAddress).pipe(map(response => {
      const index = this.dataSubject.value.data.servers.findIndex(server => server.id === response.data.server.id);
      this.dataSubject.value.data.servers[index] = response.data.server;
      this.notifier.onDefault(response.message);
      this.filterSubject.next('');
      return {
        dataState: DataState.LOADED_STATE,
        appData: this.dataSubject.value
      };
    }), startWith({
      dataState: DataState.LOADED_STATE,
      appData: this.dataSubject.value
    }), catchError(error => {
      this.filterSubject.next('');
      this.notifier.onError(error);
      return of({
        dataState: DataState.ERROR_STATE,
        error
      });
    }));
  }

  saveServer(serverForm) {
    this.isLoading.next(true);
    this.appState$ = this.serverService.save$(serverForm.value).pipe(map(response => {
      this.dataSubject.next(Object.assign(Object.assign({}, response), {
        data: {
          servers: [response.data.server, ...this.dataSubject.value.data.servers]
        }
      }));
      this.notifier.onDefault(response.message);
      document.getElementById('closeModal').click();
      this.isLoading.next(false);
      serverForm.resetForm({
        status: this.Status.SERVER_DOWN
      });
      return {
        dataState: DataState.LOADED_STATE,
        appData: this.dataSubject.value
      };
    }), startWith({
      dataState: DataState.LOADED_STATE,
      appData: this.dataSubject.value
    }), catchError(error => {
      this.isLoading.next(false);
      this.notifier.onError(error);
      return of({
        dataState: DataState.ERROR_STATE,
        error
      });
    }));
  }

  filterServers(status) {
    this.appState$ = this.serverService.filter$(status, this.dataSubject.value).pipe(map(response => {
      this.notifier.onDefault(response.message);
      return {
        dataState: DataState.LOADED_STATE,
        appData: response
      };
    }), startWith({
      dataState: DataState.LOADED_STATE,
      appData: this.dataSubject.value
    }), catchError(error => {
      this.notifier.onError(error);
      return of({
        dataState: DataState.ERROR_STATE,
        error
      });
    }));
  }

  deleteServer(server) {
    this.appState$ = this.serverService.delete$(server.id).pipe(map(response => {
      this.dataSubject.next(Object.assign(Object.assign({}, response), {
        data: {
          servers: this.dataSubject.value.data.servers.filter(s => s.id !== server.id)
        }
      }));
      this.notifier.onDefault(response.message);
      return {
        dataState: DataState.LOADED_STATE,
        appData: this.dataSubject.value
      };
    }), startWith({
      dataState: DataState.LOADED_STATE,
      appData: this.dataSubject.value
    }), catchError(error => {
      this.notifier.onError(error);
      return of({
        dataState: DataState.ERROR_STATE,
        error
      });
    }));
  }

  printReport() {
    this.notifier.onDefault('Report downloaded'); // window.print();

    let dataType = 'application/vnd.ms-excel.sheet.macroEnabled.12';
    let tableSelect = document.getElementById('servers');
    let tableHtml = tableSelect.outerHTML.replace(/ /g, '%20');
    let downloadLink = document.createElement('a');
    document.body.appendChild(downloadLink);
    downloadLink.href = 'data:' + dataType + ', ' + tableHtml;
    downloadLink.download = 'server-report.xls';
    downloadLink.click();
    document.body.removeChild(downloadLink);
  }

}

